<?php include_once 'autoload.php';
/*
| ------------------------------------------------------------------------------
| controller(string $_class);
| ------------------------------------------------------------------------------
*/
function controller($_class) {
    return Core\Layer::controller($_class);
}
/*
| ------------------------------------------------------------------------------
| model(string $_class);
| ------------------------------------------------------------------------------
*/
function model($_class) {
    return Core\Layer::model($_class);
}
/*
| ------------------------------------------------------------------------------
| view(string $file, array $data);
| ------------------------------------------------------------------------------
*/
function view($file, $data = []) {
    return Core\Layer::view($file, $data);
}
/*
| ------------------------------------------------------------------------------
| translate(string $text);
| ------------------------------------------------------------------------------
*/
function translate($text) {
    return Core\Language::translate($text);
}
/*
| ------------------------------------------------------------------------------
| print_f(mixed $data);
| ------------------------------------------------------------------------------
*/
function print_f($data) {
    return Core\Dev::print_f($data);
}
